mapper.insert:
insert into MA_USER(
NO_EMP,
USER_ID,
USER_PWD
)
values(
#{emp_no}
,#{emp_id}
,#{emp_pwd}
)

mapper.select:
select e.nm_emp as "nm_emp", e.no_emp as "no_emp", to_char(e.enter_date,'YYYY"년 "MM"월 "DD"일"') as "enter_date", to_char(e.retire_date,'YYYY"년 "MM"월 "DD"일"') as "retire_date", c.nm_cd_emp as "nm_cd_emp", d.nm_dept as "nm_dept", j.nm_jik as "nm_jik", p.email as "email", SUBSTR(p.phone,0,3)||'-'||SUBSTR(p.phone,4,4)||'-'||SUBSTR(p.phone,8,4) as "phone" from ma_emp e, ma_emp_private p, ma_dept d, ma_jikup j, ma_code_emp c where e.no_emp=p.no_emp and e.cd_dept=d.cd_dept and e.cd_jik=j.cd_jik and e.cd_emp=c.cd_emp and e.nm_emp != 'system'
and(
e.nm_emp like '%${keyword}%' or e.no_emp like '%${keyword}%' or p.email like '%${keyword}%' or p.phone like '%${keyword}%' )
and(
d.nm_dept=#{part}
)
and(
c.nm_cd_emp=#{proof}
)
and(
j.nm_jik=#{position}
)
order by e.no_emp

mapper.update:
		update ma_emp_private
		set  
        EMAIL = #{emp_email}
        ,NM_ADDR  = #{emp_loc}||#{emp_detailloc}
        ,NM_ADDR1 = #{emp_loc} 
        ,NM_ADDR2 = #{emp_detailloc} 
        ,PHONE = '${emp_phone}'  
        where no_emp = #{emp_no}
